import React, { useContext, useEffect, useState } from "react";
import AuthContext from "../context/AuthContext";
import { helpHttp } from "../helpers/helpHttp";
import first from "../media/first.png";
import second from "../media/second.png";
import third from "../media/third.png";
import mySite from "./Domain";

const NewLeaderBoard = () => {
  let { user } = useContext(AuthContext);
  const [userData, setUserData] = useState();
  const [tops, setTops] = useState([]);
  const getTop = () => {
    helpHttp()
      .get(`${mySite}topusers/`)
      .then((res) => {
        setTops(res.topuser);
      });
    helpHttp()
      .get(`${mySite}users/${user.user_id}`)
      .then((res) => {
        setUserData(res.user);
        console.log(res);
        console.log(res.user.score);
      });
  };
  useEffect(() => {
    getTop();
  }, []);

  return (
    <div className="text-black sm:w-3/4 mx-auto">
      <table class="w-full text-left bg-white shadow-lg rounded-lg">
        <thead>
          <tr class="bg-gray-800 text-white">
            <th class="px-4 py-2 text-sm font-bold uppercase tracking-wide">
              Rank
            </th>
            <th class="px-4 py-2 text-sm font-bold uppercase tracking-wide">
              Nombre
            </th>
            <th class="px-4 py-2 text-sm font-bold uppercase tracking-wide">
              XP
            </th>
          </tr>
        </thead>
        <tbody>
          <tr class="bg-gray-100">
            <td class="border px-4 py-2 text-xl">
              <img src={first} className="w-10" alt="" />
            </td>

            <td class="border px-4 py-2 text-xl font-bold">
              {tops[0] && tops[0].username}
            </td>
            <td class="border px-4 py-2 text-xl font-bold">
              {tops[0] && tops[0].score}
            </td>
          </tr>
          <tr class="bg-gray-200">
            <td class="border px-4 py-2 text-xl ">
              <img src={second} className="w-10" alt="" />
            </td>
            <td class="border px-4 py-2 text-xl font-bold">
              {tops[1] && tops[1].username}
            </td>
            <td class="border px-4 py-2 text-xl font-bold">
              {tops[1] && tops[1].score}
            </td>
          </tr>
          <tr class="bg-gray-100">
            <td class="border px-4 py-2 text-xl ">
              <img src={third} className="w-10" alt="" />
            </td>
            <td class="border px-4 py-2 text-xl font-bold">
              {tops[2] && tops[2].username}
            </td>
            <td class="border px-4 py-2 text-xl font-bold">
              {tops[2] && tops[2].score}
            </td>
          </tr>
        </tbody>
      </table>
      <button
        class="inline-block px-6 py-2.5 bg-blue-600 text-white font-medium text-xs leading-tight uppercase rounded shadow-md hover:bg-blue-700 hover:shadow-lg focus:bg-blue-700 focus:shadow-lg  focus:outline-none focus:ring-0 active:bg-blue-800 active:shadow-lg transition duration-150 ease-in-out mr-1.5"
        type="button"
        data-bs-toggle="offcanvas"
        data-bs-target="#offcanvasTop"
        aria-controls="offcanvasTop"
      >
        Toggle top offcanvas
      </button>

      <div
        class="offcanvas offcanvas-top fixed bottom-0 flex flex-col max-w-full bg-white invisible bg-clip-padding shadow-sm outline-none transition duration-300 ease-in-out text-gray-700 top-0 left-0 right-0 border-none h-1/3 max-h-full"
        tabindex="-1"
        id="offcanvasTop"
        aria-labelledby="offcanvasTopLabel"
      >
        <div class="offcanvas-header flex items-center justify-between p-4">
          <h5
            class="offcanvas-title mb-0 leading-normal font-semibold"
            id="offcanvasTopLabel"
          >
            Offcanvas top
          </h5>
          <button
            type="button"
            class="btn-close box-content w-4 h-4 p-2 -my-5 -mr-2 text-black border-none rounded-none opacity-50 focus:shadow-none focus:outline-none focus:opacity-100 hover:text-black hover:opacity-75 hover:no-underline"
            data-bs-dismiss="offcanvas"
            aria-label="Close"
          ></button>
        </div>
        <div class="offcanvas-body flex-grow p-4 overflow-y-auto">...</div>
      </div>
    </div>
  );
};

export default NewLeaderBoard;
