import "./styles/leaderboard.css";
import first from "../media/first.png";
import second from "../media/second.png";
import third from "../media/third.png";
import usuario from "../media/user.png";
import { useState } from "react";
import OutsideClickHandler from "react-outside-click-handler";
import { helpHttp } from "../helpers/helpHttp";
import { useEffect } from "react";
import Loader from "./Loader";
import { useContext } from "react";
import AuthContext from "../context/AuthContext";
import "./styles/shinytext.css";
import Modal from "react-modal";
import { isMobile } from "react-device-detect";
import mySite from "./Domain";
// import crossOut from "../media/crossout.png";

const customStyles = {
  content: {
    color: "white",
    width: isMobile ? "100%" : "500px",
    height: isMobile ? "62%" : "510px",
    border: "",
    outline: "none",
    borderRadius: "15px",
    left: "50%",
    top: "10%",
    transform: "translate(-50%)",
    backgroundColor: "#00000000",

    // top: "10px",
    transition: "2s ease-in-out",
  },
  overlay: { zIndex: 999, backgroundColor: "#18191ab1" },
};
const LeaderBoard = ({ isBoard, hideLeaderboard }) => {
  let { user } = useContext(AuthContext);
  const [userData, setUserData] = useState();
  const [tops, setTops] = useState([]);
  const getTop = () => {
    helpHttp()
      .get(`${mySite}topusers/`)
      .then((res) => {
        setTops(res.topuser);
      });
    helpHttp()
      .get(`${mySite}users/${user.user_id}`)
      .then((res) => {
        setUserData(res.user);
        console.log(res);
        console.log(res.user.score);
      });
  };

  useEffect(() => {
    getTop();
  }, []);

  return (
    <>
      <table class="table-auto w-full text-left">
        <thead>
          <tr class="bg-gray-800 text-white">
            <th class="px-4 py-2">Rank</th>
            <th class="px-4 py-2">Name</th>
            <th class="px-4 py-2">Score</th>
          </tr>
        </thead>
        <tbody>
          <tr class="bg-gray-100">
            <td class="border px-4 py-2">1</td>
            <td class="border px-4 py-2">John Doe</td>
            <td class="border px-4 py-2">100</td>
          </tr>
          <tr class="bg-gray-200">
            <td class="border px-4 py-2">2</td>
            <td class="border px-4 py-2">Jane Doe</td>
            <td class="border px-4 py-2">90</td>
          </tr>
          <tr class="bg-gray-100">
            <td class="border px-4 py-2">3</td>
            <td class="border px-4 py-2">Bob Smith</td>
            <td class="border px-4 py-2">80</td>
          </tr>
        </tbody>
      </table>
    </>
  );
};

export default LeaderBoard;
